const getters = {
    countryCode: (state) => {
        if (state.country === null) {
            const country = state.countries.find(country => (country.Name.includes(state.country))) || '';
            return country.Code
        } else {
            const country = state.countries.find(country => (country.Name.trim().toLowerCase().includes(state.country.trim().toLowerCase()))) || '';
            return country.Code
        }
    },

    shipCost: (state, getters) => {
        if (getters.countryCode === "RU") {
            return 0
        } else {
            return 1000
        }
    }

}


const actions = {
    postOrder: ({state, getters, rootGetters, commit}) => {
        let id = "667581";
        let api_key = "live_cvfT4cq0P8y1m54MKKHlqKTWwGS7EV1guzOy-9Nl4Ko";
        let session_url = 'https://cors.bridged.cc/https://api.yookassa.ru/v3/payments';
        let idem_key = uuid();
        let finalPrice = rootGetters['Cart/cartTotalPrice'] + getters.shipCost;
        return new Promise(() => {
            axios({
                method: 'post',
                url: session_url,
                headers: {'Content-Type': 'application/json', 'Idempotence-Key': idem_key},
                auth: {
                    username: id,
                    password: api_key
                },
                data: {
                    amount: {
                        value: "" + finalPrice,
                        currency: "RUB"
                    },
                    description: " : " + idem_key,
                    metadata: {
                        email: state.email,
                        phone: state.phone,
                        name: state.firstName + ' ' + state.lastName,
                        country: state.country,
                        city: state.city,
                        address: state.address,
                        postalCode: state.postalCode,
                        order: rootGetters['Cart/stringifyCart']
                    },
                    capture: false,
                    confirmation: {
                        type: "redirect",
                        return_url: "https://6og.ooo/checkout"
                    },
                }
            })
                .then(res => {
                    window.location.href= res.data.confirmation.confirmation_url;
                })
                .catch(err => {
                    alert(err);
                    commit("toggleFailModal", err);
                })
        })
    },
}


const mutations = {
    toggleSuccessModal(state) {
        state.successModal = !state.successModal
    },
    toggleFailModal(state) {
        state.failModal = !state.failModal
    },
    setShipCost(state, cost) {
        state.shipCost = cost
    },
    setValidate(state, validated) {
        state.isValidated = validated
    },
    updateEmail(state, email) {
        state.email = email
    },
    updatePhone(state, phone) {
        state.phone = phone
    },
    updateFirstName(state, firstName) {
        state.firstName = firstName
    },
    updateLastName(state, lastName) {
        state.lastName = lastName
    },
    updateAddress(state, address) {
        state.address = address
    },
    updateCity(state, city) {
        state.city = city
    },
    updateCountry(state, country) {
        state.country = country
    },
    updateRegion(state, region) {
        state.region = region
    },
    updatePostalCode(state, postalCode) {
        state.postalCode = postalCode
    }
}

export default {
    namespaced: true,
    state,
    getters,
    actions,
    mutations
}
